    @Override
    public void act() {
        ce.update();
    }
    public void stopped()
    {
        Music.bg4.setVolume(0);
    }
    public void started()
    {
        Music.bg4.setVolume(17);
    }
    /**
     * Prepare the world for the start of the program.
     * That is: create the initial objects and add them to the world.
     */
    private void prepare()
    {
        Hart hart = new Hart();
        addObject(hart,473,30);
        Hud4 h4 = new Hud4();
        addObject(h4,973,29);
    }
